import { CloudProvider } from "./constants.js";
import CreateCloudflareIntegration from '@astrojs/cloudflare';
import VeloRuntimeFetchAdapter from '@wix/runtime-fetch-adapter';
const createCloudflareAdapter = (options) => {
    return CreateCloudflareIntegration(options);
};
const createKubernetesAdapter = (options) => {
    return VeloRuntimeFetchAdapter(options);
};
const AdapterFactory = {
    [CloudProvider.CLOUD_FLARE]: createCloudflareAdapter,
    [CloudProvider.KUBERNETES]: createKubernetesAdapter
};
export { AdapterFactory };
