import { useService } from '@wix/services-manager-react';
import { ServicesListServiceDefinition } from '../services/services-list-service.js';
export const ServiceListSort = (props) => {
    const servicesListService = useService(ServicesListServiceDefinition);
    const currentSort = servicesListService.sort.get();
    // Define sort options
    const sortOptions = [
        { fieldName: 'name', order: 'ASC', label: 'Name (A-Z)' },
        { fieldName: 'name', order: 'DESC', label: 'Name (Z-A)' },
    ];
    return props.children({
        currentSort,
        sortOptions,
        setSort: (sort) => {
            servicesListService.setSort(sort);
        },
    });
};
export function ServiceListSeparateSort(props) {
    const servicesListService = useService(ServicesListServiceDefinition);
    const currentSort = servicesListService.sort.get();
    const sortFieldOptions = [{ fieldName: 'name', label: 'Name' }];
    const sortOrderOptions = [
        { order: 'ASC', label: 'Ascending' },
        { order: 'DESC', label: 'Descending' },
    ];
    return props.children({
        currentSort,
        sortFieldOptions,
        sortOrderOptions,
        setSort: (sort) => {
            servicesListService.setSort(sort);
        },
    });
}
