export const roundPrice = (amount, _currency) => Number(amount.toFixed(2));
export const formatPrice = (amount, currency, locale) => {
    try {
        return new Intl.NumberFormat(locale, {
            currency,
            style: 'currency',
        }).format(amount);
    }
    catch (error) {
        return `${amount.toFixed(2)} ${currency}`;
    }
};
