import { defineService, implementService } from '@wix/services-definitions';
import { SignalsServiceDefinition, } from '@wix/services-definitions/core-services/signals';
export const PricingOptionServiceDefinition = defineService('pricingOption');
export const PricingOptionService = implementService.withConfig()(PricingOptionServiceDefinition, ({ getService, config }) => {
    const signalsService = getService(SignalsServiceDefinition);
    const pricingOption = signalsService.signal(config.pricingOption);
    return {
        pricingOption,
    };
});
