/**
 * Runtime interface types for the Wix Runtime Fetch Adapter
 */
/**
 * The expected module structure after bundling.
 * This interface defines what the bundled output should expose.
 */
export interface ModuleWithFetch {
    default: {
        fetch: (request: Request, env?: Record<string, any>) => Promise<Response>;
    };
}
/**
 * Environment variables passed to the fetch handler.
 * These will be made available to Astro pages and API routes via Astro.locals
 */
export interface RuntimeEnvironment extends Record<string, any> {
}
/**
 * Adapter configuration options
 */
export interface WixRuntimeFetchAdapterOptions {
    /**
     * Whether to use namespaced environment access (locals.env.VAR)
     * or direct access (locals.VAR). Default: false (direct access)
     */
    namespacedEnv?: boolean;
}
/**
 * The fetch handler signature that the bundled module must expose
 */
export type FetchHandler = (request: Request, env?: RuntimeEnvironment) => Promise<Response>;
/**
 * Astro locals structure when using namespaced env (Option 1)
 */
export interface AstroLocalsWithNamespacedEnv {
    env?: RuntimeEnvironment;
}
/**
 * Astro locals structure when using direct env (Option 2)
 */
export interface AstroLocalsWithDirectEnv extends RuntimeEnvironment {
}
